$(document).ready(function () {

  // Initialize Bootstrap Tooltips for Animal of the Week
  $(function () {
    $('[data-toggle="tooltip"]').tooltip()
  })

  // Load Instagram Photo
  $('.instagram-container').on('didLoadInstagram', function (event, response) {
      var $this = $(this);
      $this.html('');
      $.each(response.data, function (i, photo) {
        $this.append('<div class="instagram" style="background-image: url('+photo.images.standard_resolution.url+');"><a target="_blank" title="'+photo.caption.text+'" href="'+photo.link+'"></a></div>');
      });
  });

  $('.instagram-container').instagram({
      userId: '1941141512',
      clientId: '0c70618abc7345fb9d778dc7ddb8b36b',
      count: 1
  });


  // Location Subnav Slide Up and Down
  $('li a.location').click(function () {
    if (!$(this).parent().hasClass('selected')) {
      $('.location').siblings('.sub-list').slideUp('fast');
      $('.location').parent().removeClass('selected');

      $(this).siblings('.sub-list').slideDown('slow');
      $(this).parent().addClass('selected');
    }
  });

  $(window).on('hashchange',function () {

    if (window.location.hash) {
      showPane(window.location.hash.slice(1));
    } else {
      hidePane();
    }
  });


  // Load Pages in Modal Pane
  $('body').on('click', 'a.hash', function (e){

    // Update Selected States
    // Slide Up Location Sub Nav if it's a main nav page
    if (!$(this).parents().hasClass('location')) {
      $('li.selected').removeClass('selected');
      $('li .sub-list').slideUp('fast');
    }
    // Update Select States for Subnav menus
    if ($(this).parents().hasClass('location')) {
      $(this).parent().siblings().removeClass('selected');
    }

    e.preventDefault();
    $('#mobile-nav-dropdown').collapse('hide');
    window.location.hash = $(this).attr('href');

    return false;
  });

  $('.close, #mask').click(function () {
    window.location.hash = '';
  });

  $('#mobile-nav-dropdown').collapse('hide')

  function hidePane (cb) {
    $('#outer-contain').addClass('off');
    $('.home-grid').removeClass('locked');

    setTimeout(function () {
      $('#outer-contain').addClass('set-back');
      if (cb) {
        cb();
      } else {
        window.location.hash = '';
      }
    }, 230);
  }

  function showPane (target) {


    // Change Browser URL
    if (!$('#outer-contain').hasClass('off')) {
      hidePane(function () {
        showPane(target);
      });
    } else {

      $('a[href="'+window.location.hash.slice(1)+'"]').parent('.page').addClass('selected');

      $('#outer-contain').removeClass('set-back');

      if(target == "/cart"){
        target = "/store/cart";
      }

      $('#inner-content').load(target+' section', function(){
        $('#outer-contain').removeClass('off');
        $('.home-grid').addClass('locked');

        // Style Select Dropdowns
        $("select").selectOrDie();

        // Init Date + Time Pickers
        $('#timepicker').datetimepicker({
            pickDate: false,
            minuteStepping: 30
        });
        $('#datepicker').datetimepicker({
            pickTime: false
        });

        resyWidget.addButton(document.getElementById('BookWithResy-rpqrgq52'), { 'venueId': 337, 'apiKey': '8HoZtBdbIPKsMfiXM8ehtiDclpq8FwOf', 'replace': true });

        Bento.GiftCards.initialize({
          successCallback: function(data) {
              window.location ="/#/cart";
          }
        });

        if ($('.post-title').length) {
          document.title = $('h1.post-title').text() + ' | Blacktree NYC';
        } else {
          document.title = $('#main-nav a[href="'+window.location.hash.slice(1)+'"]').text() + ' | Blacktree NYC';
        }


      });

    }
    return false;

  }

  if (window.location.hash.slice(1)) {
    showPane(window.location.hash.slice(1));
  }


  $(document).on("click", '.remove-item', function(event)  {
    event.preventDefault();
    $.get($(event.target).attr('href'), function(){
      location.reload(true);
    });
  });



  // Form Submission and Validation

  $(document).on("click", 'button.submit', function(event)  {
    event.preventDefault();
    var $form = $(this).parents('form');
    var error;

    function resetErrors() {
      error = false;
      $('.error-msg, .success-msg').hide();
      $form.find('input').parent().removeClass('error');
    }

    //Validations
    resetErrors();

    var required = ['email', 'firstName', 'lastName'];

    _.each(required, function (requiredId) {
      var $input = $form.find('#'+requiredId);
      if ($input.length > 0 && $input.val() === '') {
        $input.parent().addClass('error');
        error = true;
      }
    });

    if (error) {
      $form.find('.error-msg').show();
      return false;
    } else {
      var datastring = $form.serialize();

      $.ajax({
          type: 'POST',
          url: $(this).parents('form').attr('action'),
          data: datastring,
          success: function(data, textStatus) {
            resetErrors();
            $(event.target).hide();
            $form.find('.success-msg').show();
          },
          error: function(xhr, status, error){
            console.log(xhr, status, error);
          }
      });
    }
  });
});

